import React, { Component } from "react";
import { Link } from "react-router-dom";
import * as BooksAPI from "./BooksAPI";
import Shelf from "./Shelf";

class Search extends Component {
  state = {
    value: "",
    booksResult: [],
  };

  updateValue = (value) => {
    this.setState({ value: value });
    if (value.length > 0) {
      this.getBooks(value);
    } else {
      this.setState({ booksResult: [] });
    }
  };

  getBooks = async (value) => {
    const response = await BooksAPI.search(value);

    const booksResult = () =>
      response.map((element) => BooksAPI.get(element.id));

    try {
      const books = await Promise.all(booksResult());
      this.setState({ booksResult: books });
    } catch (error) {
      this.setState({ booksResult: [] });
    }
  };

  render() {
    return (
      <div className="search-books">
        <div className="search-books-bar">
          <Link to="/">
            <button className="close-search">Close</button>
          </Link>
          <div className="search-books-input-wrapper">
            <input
              type="text"
              placeholder="Search by title or author"
              value={this.state.value}
              onChange={(event) => this.updateValue(event.target.value)}
            />
          </div>
        </div>
        <div className="search-books-results" />
        <Shelf
          shelfBooks={this.state.booksResult}
          updateShelf={this.props.updateShelf}
        />
      </div>
    );
  }
}

export default Search;
